import React, { useState } from 'react';
import axios from 'axios';
import './App.css';

function App() {
  const [playlistUrl, setPlaylistUrl] = useState('');
  const handleCreate = async () => {
    window.location.href = 'http://localhost:5000/login';
  };

  // After callback, server redirects to /create_playlist and returns JSON
  React.useEffect(() => {
    const fetchResult = async () => {
      try {
        const res = await axios.get('http://localhost:5000/create_playlist', { withCredentials: true });
        setPlaylistUrl(res.data.playlist_url);
      } catch (e) {
        console.error(e);
      }
    };
    // If we're on callback route, trigger creation
    if (window.location.pathname === '/callback') {
      fetchResult();
    }
  }, []);

  return (
    <div className="p-4">
      <h1 className="text-xl font-bold mb-4">Daily Top 50 Playlist</h1>
      {!playlistUrl ? (
        <button onClick={handleCreate} className="px-4 py-2 rounded shadow">
          Connect with Spotify
        </button>
      ) : (
        <a href={playlistUrl} target="_blank" rel="noreferrer">
          View your new playlist
        </a>
      )}
    </div>
  );
}

export default App;
